INSERT INTO `movies` (`movieId`,`title`,`director`,`description`,`released`) VALUES (6,'The Godfather: Part II','Francis Ford Coppola','The early life and career of Vito Corleone in 1920s New York City is portrayed, while his son, Michael, expands and tightens his grip on the family crime syndicate.',1974);
INSERT INTO `movies` (`movieId`,`title`,`director`,`description`,`released`) VALUES (7,'The Dark Knight','Christopher Nolan','When the menace known as the Joker wreaks havoc and chaos on the people of Gotham, Batman must accept one of the greatest psychological and physical tests of his ability to fight injustice.',2008);
INSERT INTO `movies` (`movieId`,`title`,`director`,`description`,`released`) VALUES (8,'The Shawshank Redemption','Frank Darabont','Chronicles the experiences of a formerly successful banker as a prisoner in the gloomy jailhouse of Shawshank after being found guilty of a crime he did not commit. The film portrays the man\'s unique way of dealing with his new, torturous life; along the way he befriends a number of fellow prisoners, most notably a wise long-term inmate named Red.',1994);
