INSERT INTO `movies` (`movieId`,`title`,`director`,`description`,`released`) VALUES (10,'Schindler\'s List',' Steven Spielberg','In German-occupied Poland during World War II, industrialist Oskar Schindler gradually becomes concerned for his Jewish workforce after witnessing their persecution by the Nazis.',1993);
INSERT INTO `movies` (`movieId`,`title`,`director`,`description`,`released`) VALUES (11,'The Lord of the Rings: The Return of the King',' Peter Jackson','Gandalf and Aragorn lead the World of Men against Sauron\'s army to draw his gaze from Frodo and Sam as they approach Mount Doom with the One Ring.',2003);
INSERT INTO `movies` (`movieId`,`title`,`director`,`description`,`released`) VALUES (12,'Pulp Fiction',' Quentin Tarantino','The lives of two mob hitmen, a boxer, a gangster and his wife, and a pair of diner bandits intertwine in four tales of violence and redemption.',1994);
