INSERT INTO `movies` (`movieId`,`title`,`director`,`description`,`released`) VALUES (16,'Forrest Gump',' Robert Zemeckis','The presidencies of Kennedy and Johnson, the events of Vietnam, Watergate, and other history unfold through the perspective of an Alabama man with an IQ of 75.',1994);
